unitBalances.sort((a, b) => (a.Priority != null ? a.Priority : Infinity) - (b.Priority != null ? b.Priority : Infinity))

for (let i in unitBalances) {
    let unitBalance = unitBalances[i]
    let priority = unitBalance.Priority
    if (checkNaics(unitBalance, naics, RECORD_TYP_TXT, TYPOP, TAXSTAT)) {
        let tot = unitBalance['TOTAL']
        let rectot = 0
        if ([100, '100'].includes(tot)) {
            rectot = 100
        } else {
