                        record[det_f] = record[det_f].substring(0, 2) + 'X';
                    } else if (record[det_f] && record[det_f].length >= 1) {
                        record[det_f] = record[det_f].substring(0, 1) + ' X';
                    } else {
                        record[det_f] = '  X';
                    }
                }
            } else if (sum > 0 && rectot > 0) {
                // check range
                if ((Math.abs(rectot - sum) / rectot) <= tolerance) {
                    let flag_check1 = ''
                    try {
                        if (['P', 'C', 'A', 'S', 'O', 'E', 'F'].includes(record[name_f].substring(1, 2))) {
                            flag_check1 = 'Yes'
                        }
                        if (!['P', 'C', 'A', 'S', 'O', 'E', 'F'].includes(record[name_f].substring(1, 2))) {
                            flag_check1 = 'No'
                        }
                    } catch {
                        console.log('flag column does not exist: ' + name_f)
                    }
                    let flag_check2 = ''
                    for (let i = 0; i < keys.length; i++) {
                        let det_f = keys[i] + '_F'
                        try {
                            if (['P', 'C', 'A', 'S', 'O', 'E', 'F'].includes(record[det_f].substring(1, 2))) {
                                flag_check2 = 'Yes'
                            }
                            if (!['P', 'C', 'A', 'S', 'O', 'E', 'F'].includes(record[det_f].substring(1, 2))) {
                                flag_check2 = 'No'
                            }

                        } catch {
                            console.log('flag column does not exist: ' + det_f)
                        }
                    }
                    if (flag_check2 == 'Yes' && priority != 4) {
                        record[tot] = sum
                        if (record[name_f] && record[name_f].length >= 3) {
                            record[name_f] = record[name_f].substring(0, 2) + 'L' + record[name_f].substring(3);
                        } else if (record[name_f] && record[name_f].length >= 2) {
                            record[name_f] = record[name_f].substring(0, 2) + 'L';
                        } else if (record[name_f] && record[name_f].length >= 1) {
                            record[name_f] = record[name_f].substring(0, 1) + ' L';
                        } else {
                            record[name_f] = '  L';
                        }
                    } else {
                        raking(rectot, sum, keys, tolerance)
                        let value = 'CAPEX_OTH_TOT_DVAL'
                        if (keys.includes(value)) {
                            keys = arrayRemove(keys, value)
                        }
                        for (let i = 0; i < keys.length; i++) {
                            let det_f = keys[i] + '_F'
                            if (record[det_f] && record[det_f].length >= 3) {
                                record[det_f] = record[det_f].substring(0, 2) + 'K' + record[det_f].substring(3);
                            } else if (record[det_f] && record[det_f].length >= 2) {
                                record[det_f] = record[det_f].substring(0, 2) + 'K';
                            } else if (record[det_f] && record[det_f].length >= 1) {
                                record[det_f] = record[det_f].substring(0, 1) + ' K';
                            } else {
                                record[det_f] = '  K';
                            }
                        }
                    }
                } else {
                    // raking(rectot, sum, keys, tolerance)
                    for (let i = 0; i < keys.length; i++) {
                        let variable = keys[i]
                        if (Number.isNaN(parseFloat(record[variable]))) {
                            continue;
                        }
                        let det_f = keys[i] + '_F'
                        if (record[det_f] && record[det_f].length >= 3) {
                            record[det_f] = record[det_f].substring(0, 2) + 'X' + record[det_f].substring(3);
                        } else if (record[det_f] && record[det_f].length >= 2) {
                            record[det_f] = record[det_f].substring(0, 2) + 'X';
                        } else if (record[det_f] && record[det_f].length >= 1) {
                            record[det_f] = record[det_f].substring(0, 1) + ' X';
                        } else {
                            record[det_f] = '  X';
                        }
                    }
                }
            } else if (sum == 0 && rectot <= 0) {
                if (record[name_f] && record[name_f].length >= 3) {
                    record[name_f] = record[name_f].substring(0, 2) + 'X' + record[name_f].substring(3);
                } else if (record[name_f] && record[name_f].length >= 2) {
                    record[name_f] = record[name_f].substring(0, 2) + 'X';
                } else if (record[name_f] && record[name_f].length >= 1) {
                    record[name_f] = record[name_f].substring(0, 1) + ' X';
                } else {
                    record[name_f] = '  X';
                }

                for (let i = 0; i < keys.length; i++) {
                    let det_f = keys[i] + '_F'
                    if (record[det_f] && record[det_f].length >= 3) {
                        record[det_f] = record[det_f].substring(0, 2) + 'X' + record[det_f].substring(3);
                    } else if (record[det_f] && record[det_f].length >= 2) {
                        record[det_f] = record[det_f].substring(0, 2) + 'X';
                    } else if (record[det_f] && record[det_f].length >= 1) {
                        record[det_f] = record[det_f].substring(0, 1) + ' X';
                    } else {
                        record[det_f] = '  X';
                    }
                }
            } else {
            }
        }
    }
}

