const setCharAtPosition = (str, index, char) => {
    if (index > str.length - 1) return str;
    return str.substring(0, index) + char + str.substring(index + 1);
};
function setCharAt(str, index, chr) {
    if (typeof str === 'undefined' || str === null || str === '') {
        return '__' + chr;
    } else {
        if (index > str.length - 1) {
            return str.padEnd(index, '_') + chr;
        }
        return str.substring(0, index) + chr + str.substring(index + 1);
    }
}
const pathA = (record) => {
    if (record.OPSTATS === '4') {
        record.OPSTATS = 'N';
    } else if (record.OPSTATS === '5' && parseInt(record.ACTV_MIO_NUM) > 12) {
        record.OPSTATS = 'Y';
    }
};

const pathB = (record) => {
    record.OPSTATS = setCharAtPosition(record.OPSTATS || '   ', 2, 'R');
};

const pathC = (record) => {
    record.OPSTATS = setCharAtPosition(record.OPSTATS || '   ', 2, '*');
};

const pathD = (record) => {
    record.OPSTATS = 'N';
};

const pathX = (record) => {
   record['ACTION'] = "Invalid RECORD_TYP_TXT value";
};

const initACTVMIONUMAndSetOPSTATS = (record) => {
    const mioNum = parseInt(record.ACTV_MIO_NUM) || 0;
    if (mioNum === 0 || mioNum > 12) {
        record.ACTV_MIO_NUM = '12';
        record["ACTV_MIO_NUM_F"] = setCharAt(record["ACTV_MIO_NUM_F"], 2, 'J');
        record.OPSTATS = ' ';
        return pathC(record);
    } else {
        return setOPSTATS_RorAsterisk(record);
    }
};

const setOPSTATS_RorAsterisk = (record) => {
    if ((record.COVCODE === '111' || record.COVCODE === '113' || record.COVCODE === '791') &&
        parseInt(record.ACTV_MIO_NUM) < 12 &&
        record.COVC_CHG_DATE.startsWith('JAN-204')) {
        record.OPSTATS = setCharAtPosition(record.OPSTATS || '   ', 2, 'R');
    } else {
        record.OPSTATS = setCharAtPosition(record.OPSTATS || '   ', 1, '*');
    }
    return pathC(record);
};

const processRecord = (record) => {
    if (!['10', '20', '30', '90'].includes(record.RECORD_TYP_TXT)) {
        return pathX(record);
    }
    initACTVMIONUMAndSetOPSTATS(record);

    if (record.OPSTAT_STAT === '4') {
        pathD(record);
    } else if (record.OPSTAT_STAT === '5' && parseInt(record.ACTV_MIO_NUM) > 12) {
        pathA(record);
    } else {
        pathC(record);
    }
};

let record_idx = global.get("ptrModuleFacts");
const record = msg.payload.csv[record_idx];

try {
    processRecord(record);
} catch (error) {
    console.error("Error:", error.message);
    record.ACTION = "Error processing record";
}

msg.payload.csv[record_idx] = record;
return msg;

