    function setCharAt(str, index, chr) {
        if ([null, undefined].includes(str)) {
            let newstr = ''
            for (let i = 0; i < index; i++) {
                newstr = newstr + ' ';
            }
            newstr = newstr + chr
            return newstr;
        } else {
            if (index > str.length - 1) {
                let addstr = index - str.length
                let newstr = str
                for (let i = 0; i < addstr; i++) {
                    newstr = newstr + ' ';
                }
                newstr = newstr + chr
                return newstr;
            } else {
                return str.substring(0, index) + chr + str.substring(index + 1);
            }
        }
    }

    const checkOPSTAT_STAT_F = (record) => {
        const char = record?.OPSTAT_STAT_F ? record?.OPSTAT_STAT_F.charAt(0) : ' ';
        return ['R', 'T', 'K'].includes(char);
    };

    function processACTV_MIO_NUM(record) {
        let mioNum = parseInt(record?.ACTV_MIO_NUM) || 0;
        if (mioNum === 0 || mioNum > 12) {
            record.ACTV_MIO_NUM = '12';
            record.ACTV_MIO_NUM_F = setCharAt(record['ACTV_MIO_NUM_F'], 2, 'J');
        }
    }

    function determineFlow(record) {
        if ((['111', '131'].includes(record?.COV_CHG_CODE) ||
            (['791'].includes(record?.COV_CHG_CODE) && parseInt(record?.ACTV_MIO_NUM) < 12)) &&
            new Date(record?.COV_CHG_DATE) < new Date('2024-01-01')) {
            record.OPSTATS = 'N';
        } else if (checkOPSTAT_STAT_F(record)) {
            if (parseInt(record?.OPSTAT_STAT) === 4) {
                record.OPSTATS = 'N';
            } else if (parseInt(record?.OPSTAT_STAT) == 5) {
                if (parseInt(record?.RP_ACTV_MIO_NUM) == 12) {
                    record.OPSTATS = 'Y'
                } else {
                    record.OPSTATS = 'N';
                }
            } else {
                record.OPSTATS = 'Y';
            }
        } else {
            record.OPSTATS = 'Y'
        }
        if (checkOPSTAT_STAT_F(record)) {
            record.OPSTATS_F = setCharAt(record['OPSTATS_F'], 2, 'R');
        } else {
            record.OPSTATS_F = setCharAt(record['OPSTATS_F'], 2, 'J');
        }
        record.ACTION = 'Go to NONNORM Router';

    }

    function processRecord(record) {
        processACTV_MIO_NUM(record);
        if (record?.OPSTATS === ' ') {
            determineFlow(record);
        } else {
