    function calculateNAPCS(jsonD) {
        let NAPCS_BLTOT = jsonD.reduce((acc, item) => {
            if (item.PROD_CODE.substr(6, 3) === '000') {
                return acc + item.PROD_CODE_VAL;
            }
            return acc;
        }, 0);

        let NAPCS_BAL = record.RCPT_TOT_VAL - NAPCS_BLTOT;

        let NAPCS_PRM = jsonD.reduce((acc, item) => {
            if (item.PROD_CODE.startsWith('2')) {
                return acc + item.PROD_CODE_VAL;
            }
            return acc;
        }, 0);

        let NAPCS_OTH_1 = jsonD.reduce((acc, item) => {
            if (!item.PROD_CODE.startsWith('2')) {
                return acc + item.PROD_CODE_VAL;
            }
            return acc;
        }, 0);

        let NAPCS_OTH_2 = jsonD.reduce((acc, item) => {
            if (item.PROD_CODE.substr(0, 7) === '9000000') {
                return acc + item.PROD_CODE_VAL;
            }
            return acc;
        }, 0);

        let NAPCS_OTH = NAPCS_OTH_1 - NAPCS_OTH_2

        return { NAPCS_BLTOT, NAPCS_BAL, NAPCS_PRM, NAPCS_OTH };
    }

    record = { ...record, ...calculateNAPCS(jsonD) }

    msg.payload.csv[record_idx] = record
    return msg

