function setCharAt(str, index, chr) {
    if ([null, undefined].includes(str)) {
        let newstr = ''
        for (let i = 0; i < index; i++) {
            newstr = newstr + ' ';
        }
        newstr = newstr + chr
        return newstr;
    } else {
        if (index > str.length - 1) {
            let addstr = index - str.length
            let newstr = str
            for (let i = 0; i < addstr; i++) {
                newstr = newstr + ' ';
            }
            newstr = newstr + chr
            return newstr;
        } else {
            return str.substring(0, index) + chr + str.substring(index + 1);
        }
    }
}

