            item.program === "aies" &&
            item.unit_type === "all"
    )?.json_values;

    function resetNAPCS(row) {
        if (row?.PROD_CODE) {
            if (parseFloat(row?.PROD_CODE_VAL) == 0) {
                row.USABLE = 'D'
            } else if (parseFloat(row?.PROD_CODE_VAL) < 0) {
                row.USABLE = 'I'
            } else {
                record.NAPCS_IND = 'U'
                if (parseFloat(row?.PROD_CODE_VAL) > 0) {
                    if (row?.PROD_CODE.substring(0, 7) == '9000000') {
                        row.USABLE = 'J'
                    } else {
                        row.USABLE = 'U'
                    }
                }
            }
        }
    }

    function imputeHist() {
        if (record?.NAPCS_IMP == 'Y') {
            for (let i in allData) {
                let row = allData[i]
                if (row?.REPORTING_ID == id) {
                    if (row?.PROD_CODE_VAL_F_PI && row?.PROD_CODE_VAL_F_PI.charAt(1) != ' ') {
                        record.NAPCS_IMP = ' '
                    } else if (row?.PROD_CODE && (!(row?.PROD_CODE_VAL_F_PI))) {
                        record.NAPCS_IMP = ' '
                    }
                }
            }
        } else if (record?.NAPCS_IND == ' ') {
            if (record?.HISTSTAT == 'Y' && record?.EC_NAPCS_IND == 'U' && record?.EC_NAPCS_BLTOT > 0 && record?.EC_RCPT_TOT_VAL > 0) {
                for (let i in allData) {
                    let row = allData[i]
                    if (row?.EC_REPORTING_ID == id) {
                        if (parseFloat(row?.EC_NAPCS_PROD_CODE_VAL) > 0) {
                            if (['I', 'D', 'X'].includes(row?.EC_NAPCS_USABLE)) {
                            } else {
                                record.NAPCS_IMP = 'Y'
                            }
                        }
                    }
                }
            }
        }
    }

    let id = record['REPORTING_ID']

    let allData = msg.payload.csv;
    let NAPCS_BLTOT = 0
    let NAPCS_PRM = 0
    let NAPCS_OTH = 0
    let ProdFound = 'N'

    if (record?.NAICS_SECTOR == '31' && record?.SUBSAMP_IND_TXT != 'MC') {
        for (let i in allData) {
            let row = allData[i]
            if (row.REPORTING_ID == id) {
                resetNAPCS(row)
                if (row?.PROD_CODE) {
                    ProdFound = 'Y'
                }
            }
        }
        if (ProdFound == 'N') {
            record['NAPCS_IND'] = ' '
        }

        imputeHist()

        let val_9000 = ''
        for (let i in allData) {
            let row = allData[i]
            if (row.REPORTING_ID == id) {
                if (row?.PROD_CODE) {
                    if (['I', 'D', 'X'].includes(row?.USABLE)) {
                    } else {
                        if (row?.PROD_CODE.substring(7, 10) == '000') {
                            NAPCS_BLTOT = NAPCS_BLTOT + parseFloat(row?.PROD_CODE_VAL)
                        }
                        if (row?.PROD_CODE.substring(0, 1) == '2') {
                            NAPCS_PRM = NAPCS_PRM + parseFloat(row?.PROD_CODE_VAL)
                        }
                        if (row?.PROD_CODE.substring(0, 1) != '2') {
                            if (row.PROD_CODE.substring(0, 7) == '9000000') {
                            } else {
                                NAPCS_OTH = NAPCS_OTH + parseFloat(row?.PROD_CODE_VAL)
                            }
                        }
                        let napcs = naics_napcs.NAPCS_PREP.some(element => (parseInt(element.NAPCS) === parseInt(row?.PROD_CODE)));
                        if (napcs) {
                        } else {
                            row.USABLE = 'I'
                        }
                    }
                    if (row?.PROD_CODE == '9000000000') {
                        val_9000 = row?.PROD_CODE_VAL
                    }
                }
            }
        }

        record['NAPCS_BLTOT'] = NAPCS_BLTOT
        record['NAPCS_BAL'] = parseFloat(record?.RCPT_TOT_VAL) - NAPCS_BLTOT
        record['NAPCS_PRM'] = NAPCS_PRM
        record['NAPCS_OTH'] = NAPCS_OTH

        if (val_9000 != '') {
            if (parseFloat(val_9000) > (parseFloat(NAPCS_BLTOT) - parseFloat(val_9000))) {
                record['FLAG'] = 'WRITEIN_IND_DETER'
            }
        }

        record.ACTION = 'END'
    } else {
        record.ACTION = 'Go to Main Routing'
    }

    return msg;
}catch(e){
    return msg;
}

