function sumOfVariables(total, arr) {
    let sumDET = 0;
    for (let i = 0; i < arr.length; i++) {
        sumDET += arr[i];
    }    

    if (sumDET > 0 && total == 0 ) {
        total = sumDET
        msg.payload.total_flg ? msg.payload.total_flg + 'L' : msg.payload.total_flg = 'L'
    } else if (sumDET == 0 && total > 0 ) {
        total = sumDET
        msg.payload.details_flg ? msg.payload.details_flg + 'X' : msg.payload.details_flg = 'X'
    } else if (sumDET > 0 && total > 0) {
        let ratio = sumDET / total;
        if (ratio >= 0.8 && ratio <= 1.2) {
            msg.payload.details_flg ? msg.payload.details_flg + 'X' : msg.payload.details_flg = 'X'
        } else {
            total = sumDET
            msg.payload.details_flg ? msg.payload.details_flg + 'X' : msg.payload.details_flg = 'X'
        }
    } else if (sumDET == 0 && total == 0 ) {
        msg.imputations = true
        msg.payload.details_flg ? msg.payload.details_flg + 'X' : msg.payload.details_flg = 'X'
    }
}

