function setCharAt(str, index, chr) {
    if ([null, undefined].includes(str)) {
        let newstr = ''
        for (let i = 0; i < index; i++) {
            newstr = newstr + ' ';
        }
        newstr = newstr + chr
        return newstr;
    } else {
        if (index > str.length - 1) {
            let addstr = index - str.length
            let newstr = str
            for (let i = 0; i < addstr; i++) {
                newstr = newstr + ' ';
            }
            newstr = newstr + chr
            return newstr;
        } else {
            return str.substring(0, index) + chr + str.substring(index + 1);
        }
    }
}


function initializeVariables(record) {
    record.PAY_ANN_VAL_F = setCharAt(record.PAY_ANN_VAL_F, 2, 'X');
    record.RCPT_TOT_VAL_F = setCharAt(record.RCPT_TOT_VAL_F, 2, 'X');
    record.EMP_MAR12_NUM_F = setCharAt(record.EMP_MAR12_NUM_F, 2, 'X');
    record.PAY_QTR1_VAL_F = setCharAt(record.PAY_QTR1_VAL_F, 2, 'U');
    record.PAY_ANN_VAL = 0
    record.RCPT_TOT_VAL = 0
    record.EMP_MAR12_NUM = 0
    record.PAY_QTR1_VAL = 0
    return record;
}

