function checkNaics(data, naics, rec_typ, rec_typop, rec_tax) {
    let list = data['NAICS']
    let unit = data['UNIT TYPE']
    let typop = data['TYPOP']
    let tax = data['TAXSTAT']
    let rectyp = rec_typ
    if (['20', '90', 20, 90].includes(rectyp)) {
        rec_typ = 'ESTAB'
    } else if (['30', 30].includes(rectyp)) {
        rec_typ = 'KAU'
    } else if ([10, '10'].includes(rectyp)) {
        rec_typ = 'ENT'
    }
    if ([null, undefined, 'null', 'undefined'].includes(typop)) {
        rec_typop = typop
    }
    if ([null, undefined, 'null', 'undefined'].includes(tax)) {
        rec_tax = tax
    }
    // console.log(rec_tax, tax, rec_typop, typop)
    if (rec_typ === unit && rec_tax === tax && rec_typop === typop) {
        if (["ALL", "All"].includes(list)) {
            return (true);
        } else {
            list = list.replace(/ /g, "");
            list = list.replace(/'/g, "");
            var array = list.split(',')

            if (array.find(r => naics.startsWith(r))) {
                return (true);
            } else {
                return (false)
            }
        }
    }
}

function sumRow(data) {
    let sum = 0;
    var keys = [];
    for (var key in data) {
        if (key.startsWith('DETAIL') && data[key] != null) {
            let variable = data[key]
            // console.log(variable)
            if (!Number.isNaN(parseFloat(record[variable]))) {
                sum = parseFloat(sum) + parseFloat(record[variable])
            }
            // else {
            //     record[variable] = 0
            // }
            keys.push(variable)
        }
    }
    return [sum, keys];
}

function raking(rectot, sum, keys, tol) {
    if ((Math.abs(rectot - sum) / rectot) <= tol) {
        keys.sort((a, b) => record[b] - record[a]);
        let remainder = 0
        for (let i = 0; i < keys.length; i++) {
            let num1 = (rectot / sum) * parseFloat(record[keys[i]]) + remainder
            let num2 = Math.round(num1)
            remainder = num1 - num2
            record[keys[i]] = num2
        }
    }
}

