function keysToUpperCase(obj) {
    const output = {};
    for (let key in obj) {
        if (Object.prototype.hasOwnProperty.call(obj, key)) {
            if (typeof obj[key] === 'object' && obj[key] !== null) {
                output[key.toUpperCase()] = keysToUpperCase(obj[key]);
            } else {
                output[key.toUpperCase()] = obj[key];
            }
        }
    }
    return output;
}

