            type: 'json',
            source: ['body'],
            required: true,
            default: []
        },
        message: {
            type:'string',
            source: ['body'],
            required: true,
            default: ''
        },
        sentFrom: {
            type:'string',
            source: ['body', 'params', 'query'],
            required: true,
            default: 'Qbes'
        },

        sentTo: {
            type:'string',
            source: ['body', 'params', 'query'],
