msg.query = `select id, name, description, active, type, rvs,    parsed_rule->>'event' as event ,parsed_rule->>'conditions' as conditions,  (parsed_rule)::TEXT parsed_rule, data   from aies.rules_repository where type like '%' `
msg.topic = 'facts'

return msg;

msg.query = `select id, name, description, active, type, rvs,    parsed_rule->>'event' as event ,parsed_rule->>'conditions' as conditions,  (parsed_rule)::TEXT parsed_rule, data   from aies.rules_repository where type = 'KAU/ESTAB' `
msg.topic = 'facts'

return msg;

msg.query = `select id, name, description, active, type, rvs,    parsed_rule->>'event' as event ,parsed_rule->>'conditions' as conditions,  (parsed_rule)::TEXT parsed_rule, data   from aies.rules_repository where type = 'estab' `
msg.topic='facts'

return msg;

msg.payload = msg.payload.rows
msg.topic = 'rules'
return msg;

msg.factsArray = [msg.facts]
msg.payload = {factsArray: [msg.facts], facts: msg.facts, rules: msg.rules}
return msg;

context.data = context.data || new Object();

switch (msg.topic) {
    case "facts":
        context.data.facts = msg.payload.data[0].facts;
        msg = null;
        break;
    case "rules":
        context.data.rules = msg.payload;
        msg = null;
        break;
    case "task3":
        context.data.task3 = msg.payload;
        msg = null;
        break;

    default:
        msg = null;
    	break;

}

if(context.data.rules != null && context.data.facts != null && context.data.task3 == null) {
	let msg2 = new Object();
    msg2 = context.data;
    context.data=null;
	return msg2;
} else return msg;

// msg.payload.facts = msg.payload.data[0].facts
msg.topic = 'facts'
return msg;

msg.actions.allUnitsCompleted = true
msg.actions.determineStatusCompleted = true
return msg;

let preRules = []

msg.payload.map((r, k) => {


// Build conditionstring cs
let cs  = r['CONDITION'] || 'PAY_ANN > 0'

let shortCondition = r['Short Condition'] || ''

cs = (shortCondition == '') ? cs : shortCondition + ' and ' + cs




    let actionStringRHS = ''
    let action =[] // [{ 'FAILURE': true }]
    let actionString = (r['ACTIONSTRING']).trim()

    // actionString = (actionString.endsWith(";")) ? actionString.slice(0, -1) + "" : ""





    let arr = actionString.split(";");

    for (let i = 0; i < arr.length; i++) {
        const equation = arr[i];
        const [key, val] = equation.split(/(?<=^[^\=]+)\=/) //split("=");


        let c_key = key.replace(/[^\x20-\x7E]/g, "").trim()
        let c_value = val.replace(" eq ", "=").trim()

        if (key)
            action.push({ [c_key]: c_value });
    }


// GET DETAIL TOTALS:
let SUM_DET = ''
let SUM_DET_ARRAY = []




for(let j = 0 ; j < 14; ++j){

    let detVar = r['DETAIL ' + j] ? r['DETAIL ' + j] : ''
   if(detVar !=='') SUM_DET_ARRAY.push(detVar) 
}


if(SUM_DET_ARRAY.length > 0){
    action.push({ ["TOTAL"]: r['TOTAL'] }) 
SUM_DET = SUM_DET_ARRAY.join(' + ')
action.push({"SUM_DET":SUM_DET })


}
    let sector = r['SECTOR/TRADE'] ? r['SECTOR/TRADE'] : "All"
    let validationType = "Sector "+sector



 //&& r['ACTIONSTRING'] != '' ? r['ACTIONSTRING'] :  [{'FAILURE': true}]
    preRules.push({ 
    key: k+2, 
    ruleId: k+2,
    type: k+2,
    priority: r['PRIORITY'] || 5,
    conditionstring:  cs ,
    message: r['DESCRIPTION'], 
    action,
    actionString: JSON.stringify(action),
    validationType,
    sector
    })

})

msg.preRules = preRules
msg.params = preRules
msg.payload = {paramFile: preRules}
return msg;

    // function to convert string of equations to JSON array
    function stringToJSON (str){


        // function cleanupString(str) { return str.replace(/[^\x20-\x7E]/g, "").trim(); }

    let arr = str.split(";");
    let json = [];
    for (let i = 0; i < arr.length; i++) {
        const equation = arr[i];
        const [key, val] = equation.split(/(?<=^[^\=]+)\=/) //split("=");

        if (key)
            json.push({ [cleanupString(key)]: cleanupString(val.replace(" eq ", "=")) });
    }
    return json;
}

function cleanupString(str){ return str.replace(/[^\x20-\x7E]/g, "").trim(); }

// NK will set the submitted flow to null

// flow.set("submitted", null)
// initialise the counter to 0 if it doesn't exist already
var dashboardLog = context.get('dashboardLog')|| [];

dashboardLog.push(msg);
if (dashboardLog.length > 20){
    // Delete oldest message if > 20
    dashboardLog.shift();
    dashboardLog.length = 20;
} 


// store the value back
context.set('dashboardLog',dashboardLog);

// make it part of the outgoing msg object
msg = {};
msg.payload = [...dashboardLog];
return msg;

context.set('dashboardLog', []);
msg.payload = []
return msg;

msg.payload.csv[0].action = "SET IMPUTATION FLAG?"
msg.payload.csv[0]["Varx-IMBALANCE"] = true
return msg;

if ((msg.payload.csv[0].RECORD_TYP === "00" || msg.payload.csv[0].RECORD_TYP === "10" || msg.payload.csv[0].RECORD_TYP === "20" || msg.payload.csv[0].RECORD_TYP === "30" || msg.payload.csv[0].RECORD_TYP === "90") && msg.payload.csv[0].BR_STAT === "N") {
    msg.payload.csv[0].RECORD_TYP = "BR_DELETE";
} else if (Number(msg.payload.csv[0].RECORD_TYP) >= 20 && Number(msg.payload.csv[0].RECORD_TYP) <= 90 ) {
    msg.payload.csv[0].action = "Call Critical Errors module"
}

msg.payload.csv[0].action = "Go to Determine Critical Error module"

return msg;

if (msg.payload.csv[0].RECORD_TYP === "20" || msg.payload.csv[0].RECORD_TYP === "90") {
    if(msg.payload.csv[0].PROCESS_DIV_CODE = "1") {
        msg.payload.csv[0].action = "Go to: Derive MFG NAICS from NAPCS"
    } else {
        msg.payload.csv[0].action = "Go to: Classification"
    }
} else {
    msg.payload.csv[0].action = "Go to: Classification"
}
return msg;

node.status({ fill: "green", shape: "dot", text: "processing" });

// msg.payload.csv[0].module = "_"

