return msg;

node.status({ fill:"green", shape:"dot", text: "processing" });

if ((msg.payload.RECORD_TYP === "00" || msg.payload.RECORD_TYP === "10" || msg.payload.RECORD_TYP === "20" || msg.payload.RECORD_TYP === "30" || msg.payload.RECORD_TYP === "90") && msg.payload.BR_STAT === "N") {
    msg.payload.RECORD_TYP = "BR_DELETE";
} else if (Number(msg.payload.RECORD_TYP) >= 20 && Number(msg.payload.RECORD_TYP) <= 90 ) {
    msg.payload.action = "Call Critical Errors module"
}

msg.payload.action = "Go to Determine Critical Error module"

msg.payload.module = "BR Stat Evaluation"

