return msg;

node.status({fill:"green",shape:"dot",text:"processing"});

// Assign sample values to variables
//setinit variables (first appearance)
var ACTV_MIO_NUM = msg.payload.csv[0].ACTV_MIO_NUM
var ACTV_MIO_NUM_FLG = msg.payload.csv[0].ACTV_MIO_NUM_FLG
var OPSTATS = msg.payload.csv[0].OPSTATS
var OPSTATS_FLG = msg.payload.csv[0].OPSTATS_FLG
var COV_CHG_CODE = msg.payload.csv[0].COV_CHG_CODE
var ACTVMIO_R = msg.payload.csv[0].ACTVMIO_R
var COV_CHG_DATE = new Date(msg.payload.csv[0].COV_CHG_DATE)
var OPSTAT = msg.payload.csv[0].OPSTAT
var OPSTAT_FLAG = msg.payload.csv[0].OPSTAT_FLAG

//nonorm router Variables (first appearance)
var RECORD_TYP = msg.payload.csv[0].RECORD_TYP
var BMF_FR941 = msg.payload.csv[0].BMF_FR941
var EPPAYANN = msg.payload.csv[0].EPPAYANN
var NONNORM_FLG = msg.payload.csv[0].NONNORM_FLG
var NONNORM = msg.payload.csv[0].NONNORM
var PAY_QTR1_VAL = msg.payload.csv[0].PAY_QTR1_VAL
var EMP_MAR12_NUM = msg.payload.csv[0].EMP_MAR12_NUM
var OPSTATS_F = msg.payload.csv[0].OPSTATS_F

// Flow: set_OPSTATS_ACTIV_MIO_NUM (first appearance)
var TEMPMIO = msg.payload.csv[0].TEMPMIO
var ECPAYQ1 = msg.payload.csv[0].ECPAYQ1
var EPPAYANN = msg.payload.csv[0].EPPAYANN
var ACTV_MIO_VAL = msg.payload.csv[0].ACTV_MIO_VAL
var ACTV_MIO_VAL_FLG = msg.payload.csv[0].ACTV_MIO_VAL_FLG
var BMF_FR941 = msg.payload.csv[0].BMF_FR941
var ECPAYQ2 = msg.payload.csv[0].ECPAYQ2
var ECPAYQ3 = msg.payload.csv[0].ECPAYQ3
var ECPAYQ4 = msg.payload.csv[0].ECPAYQ4
var ECPAYQ4_F = msg.payload.csv[0].ECPAYQ4_F

// Flow: set_NONNORM (first appearance)
var REFPER = msg.payload.csv[0].REFPER
var MADBPAYANN = msg.payload.csv[0].MADBPAYANN
var EMP_MAR12_NUM_F = msg.payload.csv[0].EMP_MAR12_NUM_F
var PAY_ANN_VAL = msg.payload.csv[0].PAY_ANN_VAL

// Flow: set_BASIC_0 (first appearance)
var PAY_QTR1_VAL_R = msg.payload.csv[0].PAY_QTR1_VAL_R
var PAY_QTR1_VAL_FLG = msg.payload.csv[0].PAY_QTR1_VAL_FLG
var EMP_MAR12_NUM = msg.payload.csv[0].EMP_MAR12_NUM
var EMP_MAR12_NUM_FLG = msg.payload.csv[0].EMP_MAR12_NUM_FLG

// Flow: SPECIAL_SEASON (first appearance)
var CY_PAY_QTR1_VAL = msg.payload.csv[0].CY_PAY_QTR1_VAL
var ECPAYQ1ZERO = msg.payload.csv[0].ECPAYQ1ZERO
var CY_EMP_MAR12_NUM = msg.payload.csv[0].CY_EMP_MAR12_NUM
var ECEMPQ1 = msg.payload.csv[0].ECEMPQ1

// Function to process the payload
function processPayload() {
    var result = {}
    function pathX() {
        if (OPSTATS === '') {
            return pathY();
        } else {
            return pathF();
        }
    }
    // Main logic flow
    if (ACTV_MIO_NUM < 0 || ACTV_MIO_NUM > 12) {
        ACTV_MIO_NUM = 12;
        ACTV_MIO_NUM_FLG = ACTV_MIO_NUM_FLG.substring(1, 2) + 'J' + ACTV_MIO_NUM_FLG.substring(2, 3);
        result.payload = pathX();
        result.ACTV_MIO_NUM = ACTV_MIO_NUM;
        result.ACTV_MIO_NUM_FLG = ACTV_MIO_NUM_FLG;
        result.OPSTATS = OPSTATS;
        result.OPSTATS_FLG = OPSTATS_FLG;
        result.COV_CHG_CODE = COV_CHG_CODE;
        result.ACTVMIO_R = ACTVMIO_R;
        result.COV_CHG_DATE = COV_CHG_DATE;
        result.OPSTAT = OPSTAT;
        result.OPSTAT_FLAG = OPSTAT_FLAG;
        result.RECORD_TYP = RECORD_TYP;
        result.BMF_FR941 = BMF_FR941;
        result.EPPAYANN = EPPAYANN;
        result.NONNORM_FLG = NONNORM_FLG;
        result.NONNORM = NONNORM;
        result.PAY_QTR1_VAL = PAY_QTR1_VAL;
        result.EMP_MAR12_NUM = EMP_MAR12_NUM;
        result.OPSTATS_F = OPSTATS_F
        result.TEMPMIO = TEMPMIO ;
        result.ECPAYQ1 = ECPAYQ1;
        result.EPPAYANN = EPPAYANN;
        result.ACTV_MIO_VAL = ACTV_MIO_VAL;
        result.ACTV_MIO_VAL_FLG = ACTV_MIO_VAL_FLG;
        result.BMF_FR941 = BMF_FR941;
        result.ECPAYQ2 = ECPAYQ2;
        result.ECPAYQ3 = ECPAYQ3;
        result.ECPAYQ4 = ECPAYQ4;
        result.ECPAYQ4_F = ECPAYQ4_F;
        result.REFPER = REFPER;
        result.MADBPAYANN = MADBPAYANN;
        result.EMP_MAR12_NUM_F = EMP_MAR12_NUM_F;
        result.PAY_ANN_VAL = PAY_ANN_VAL;
        result.PAY_QTR1_VAL_R = PAY_QTR1_VAL_R;
        result.PAY_QTR1_VAL_FLG = PAY_QTR1_VAL_FLG;
        result.EMP_MAR12_NUM = EMP_MAR12_NUM;
        result.EMP_MAR12_NUM_FLG = EMP_MAR12_NUM_FLG;
        result.CY_PAY_QTR1_VAL = CY_PAY_QTR1_VAL;
        result.ECPAYQ1ZERO = ECPAYQ1ZERO;
        result.CY_EMP_MAR12_NUM = CY_EMP_MAR12_NUM;
        result.ECEMPQ1 = ECEMPQ1;
    } else {
        result.payload = pathX();
        result.ACTV_MIO_NUM = ACTV_MIO_NUM;
        result.ACTV_MIO_NUM_FLG = ACTV_MIO_NUM_FLG;
        result.OPSTATS = OPSTATS;
        result.OPSTATS_FLG = OPSTATS_FLG;
        result.COV_CHG_CODE = COV_CHG_CODE;
        result.ACTVMIO_R = ACTVMIO_R;
        result.COV_CHG_DATE = COV_CHG_DATE;
        result.OPSTAT = OPSTAT;
        result.OPSTAT_FLAG = OPSTAT_FLAG;
        result.RECORD_TYP = RECORD_TYP;
        result.BMF_FR941 = BMF_FR941;
        result.EPPAYANN = EPPAYANN;
        result.NONNORM_FLG = NONNORM_FLG;
        result.NONNORM = NONNORM;
        result.PAY_QTR1_VAL = PAY_QTR1_VAL;
        result.EMP_MAR12_NUM = EMP_MAR12_NUM;
        result.OPSTATS_F = OPSTATS_F
        result.TEMPMIO = TEMPMIO;
        result.ECPAYQ1 = ECPAYQ1;
        result.EPPAYANN = EPPAYANN;
        result.ACTV_MIO_VAL = ACTV_MIO_VAL;
        result.ACTV_MIO_VAL_FLG = ACTV_MIO_VAL_FLG;
        result.BMF_FR941 = BMF_FR941;
        result.ECPAYQ2 = ECPAYQ2;
        result.ECPAYQ3 = ECPAYQ3;
        result.ECPAYQ4 = ECPAYQ4;
        result.ECPAYQ4_F = ECPAYQ4_F;
        result.REFPER = REFPER;
        result.MADBPAYANN = MADBPAYANN;
        result.EMP_MAR12_NUM_F = EMP_MAR12_NUM_F;
        result.PAY_ANN_VAL = PAY_ANN_VAL;
        result.PAY_QTR1_VAL_R = PAY_QTR1_VAL_R;
        result.PAY_QTR1_VAL_FLG = PAY_QTR1_VAL_FLG;
        result.EMP_MAR12_NUM = EMP_MAR12_NUM;
        result.EMP_MAR12_NUM_FLG = EMP_MAR12_NUM_FLG;
        result.CY_PAY_QTR1_VAL = CY_PAY_QTR1_VAL;
        result.ECPAYQ1ZERO = ECPAYQ1ZERO;
        result.CY_EMP_MAR12_NUM = CY_EMP_MAR12_NUM;
        result.ECEMPQ1 = ECEMPQ1;
    }
    return result;
}
function pathF() {
    if (OPSTATS_FLG.substring(0, 2) !== 'R ' && OPSTATS_FLG.substring(1, 2) !== 'C') {
        return pathY();
    } else {
        return nonNormRouter();
    }
}

function pathY() {
    if ((COV_CHG_CODE === 111 || COV_CHG_CODE === 131 || (COV_CHG_CODE === 791 && ACTVMIO_R < 12)) && COV_CHG_DATE < new Date('2024-01-01')) {
        return pathA();
    } else if (OPSTAT_FLAG.substring(0, 1) === 'R') {
        return pathZ();
    } else {
        return pathB();
    }
}

function pathA() {
    OPSTATS = 'N';
    return pathD();
}

function pathZ() {
    if (OPSTAT === 4) {
        return pathA();
    } else if (OPSTAT === 5) {
        return pathE();
    } else {
        return pathB();
    }
}

function pathE() {
    if (ACTV_MIO_NUM === 12) {
        return pathB();
    } else {
        return pathA();
    }
}

function pathB() {
    OPSTATS = 'Y';
    return pathD();
}

function pathD() {
    if (OPSTAT_FLAG.substring(0, 1) === 'R') {
        OPSTATS_FLG = 'R' + OPSTATS_FLG.substring(1);
        return nonNormRouter();
    } else {
        OPSTATS_FLG = OPSTATS_FLG.substring(0, 1) + 'J' + OPSTATS_FLG.substring(2);
        return nonNormRouter();
    }
}

function nonNormRouter() {
    if (ACTV_MIO_NUM_FLG.substring(0, 1) === 'R') {
        return pathG();
    } else {
        return pathH();
    }
}


function pathG() {
    if (RECORD_TYP === 90) {
        return pathI();
    } else {
        return pathH();
    }
}

function pathH() {
    if (BMF_FR941 === 97 || BMF_FR941 === 98 || BMF_FR941 === 99) {
        return pathA1();
    } else {
        return pathL();
    }
}

function pathI() {
    if (ACTV_MIO_NUM < 12 && EPPAYANN === 0) {
        NONNORM = 'B';
        NONNORM_FLG = NONNORM_FLG.substring(0, 1) + 'L';
        return pathA1();
    } else {
        return pathA1();
    }
}

function pathL() {
    if (ACTV_MIO_NUM_FLG.substring(1, 2) !== 'C' && OPSTATS_F.substring(1, 2) !== 'C') {
        return set_OPSTATS_ACTIV_MIO_NUM() + pathA1();
    } else {
        return pathA1();
    }
}

function pathA1() {
    if (NONNORM_FLG === '' || NONNORM_FLG.substring(1, 2) === 'J') {
        return set_NONNORM();
    } else {
        return pathN();
    }
}

function pathN() {
    if (NONNORM === 'B') {
        return set_BASIC_0();
    } else {
        return pathO();
    }
}

function pathO() {
    if (NONNORM === 'S' && ACTV_MIO_NUM < 10 && PAY_QTR1_VAL === 0 && EMP_MAR12_NUM === 0) {
        return SPECIAL_SEASON();
    } else {
        return ("Exit to AEIS main");
    }
}

// Flow: set_OPSTATS_ACTIV_MIO_NUM
function set_OPSTATS_ACTIV_MIO_NUM() {
    if (RECORD_TYP === 90) {
        TEMPMIO = 0;
        return pathP();
    } else if (OPSTATS_FLG === 'R ' && ACTV_MIO_NUM_FLG === 'R ') {
        return pathA2();
    } else {
        return pathQ();
    }
}


function pathP() {
    if (PAY_QTR1_VAL + ECPAYQ1 > 0) {
        TEMPMIO += 3;
        return pathD1();
    } else if (EPPAYANN > 0) {
        return pathD1();
    } else {
        NONNORM = 'B';
        NONNORM_FLG = NONNORM_FLG.substring(0, 1) + 'L';
        return pathD1();
    }
}

function pathA2() {
    return nonNormRouter();
}

function pathQ() {
    if ([211, 221, 241, 311, 321, 341, 401, 511, 741].includes(COV_CHG_CODE)) {
        return pathR();
    } else if ([111, 131, 791].includes(COV_CHG_CODE)) {
        return pathS();
    } else {
        return pathA2();
    }
}

function pathD1() {
    if (ECPAYQ2 > 0) {
        TEMPMIO += 3;
        return pathT();
    } else {
        return pathT();
    }
}

function pathT() {
    if (ECPAYQ3 > 0) {
        TEMPMIO += 3;
        return pathU();
    } else {
        return pathU();
    }
}

function pathU() {
    if (ECPAYQ4 > 0 || ECPAYQ4_F === '') {
        TEMPMIO += 3;
        return pathV();
    } else if (OPSTATS_FLG === 'R ') {
        return pathB1();
    } else {
        return pathC1();
    }
}

function pathV() {
    if ([3, 51, 0].includes(BMF_FR941)) {
        return pathC1();
    } else {
        return pathB1();
    }
}

function pathB1() {
    ACTV_MIO_NUM = TEMPMIO;
    ACTV_MIO_NUM_FLG = ACTV_MIO_NUM_FLG.substring(0, 1) + 'L ';
    return pathA2();
}

function pathC1() {
    OPSTATS = 'N';
    OPSTATS_FLG = OPSTATS_FLG.substring(0, 1) + 'L ';
    return pathB1();
}

function pathR() {
    if (COV_CHG_DATE > new Date('2023-01-31') && COV_CHG_DATE < new Date('2024-01-01')) {
        ACTV_MIO_VAL = 13 - new Date(COV_CHG_DATE).getMonth();
        ACTV_MIO_VAL_FLG = ACTV_MIO_VAL_FLG.substring(0, 1) + 'L ';
        return pathA2();
    } else {
        return pathA2();
    }
}

function pathS() {
    if (COV_CHG_DATE < new Date('2024-01-01')) {
        ACTV_MIO_NUM = new Date(COV_CHG_DATE).getMonth() + 1;
        ACTV_MIO_NUM_FLG = ACTV_MIO_NUM_FLG.substring(0, 1) + 'L ';
        OPSTATS = 'N';
        OPSTATS_FLG = OPSTATS_FLG.substring(0, 1) + 'L ';
        return pathA2();
    } else {
        return pathA2();
    }
}


// Flow: set_NONNORM
function set_NONNORM() {
    NONNORM = '';
    if (OPSTATS === 'Y') {
        return pathW();
    } else {
        NONNORM = 'D';
        NONNORM_FLG = NONNORM_FLG.substring(0, 1) + 'J';
        return pathA2();
    }
}


function pathW() {
    if (REFPER > 2023) {
        return path1();
    } else if (RECORD_TYP === 20 && ACTV_MIO_NUM < 12 && (ACTV_MIO_NUM_FLG.substring(1, 2) === 'L' || (MADBPAYANN === 0 && REFPER === 2022))) {
        return pathB2();
    } else {
        return path2();
    }
}

function path1() {
