    // Update existing rows and/or add new rows
    for (let key in preData) {
        if (preData.hasOwnProperty(key)) {
            let row = findRow(key);
            if (!row) {
                row = {
                    "Variable": key,
                    "Pre": preData[key],
                    "Post": "Not Available",
                    "Changed_Indicator": ""
                };
                tableData.push(row);
            } else {
                row.Pre = preData[key];
            }
        }
    }

    // Handle "post" data
} else if (msg.topic === "post") {
    let postData = msg.payload.post;

    // Update existing rows with post data
    tableData.forEach(function (row) {
        if (postData.hasOwnProperty(row.Variable)) {
            row.Post = postData[row.Variable];
            if (row.Pre !== "Not Available" && row.Pre !== row.Post) {
                row.Changed_Indicator = "Y";
            }
        }
    });

    // Add new rows for new post data variables
    for (let key in postData) {
        if (postData.hasOwnProperty(key)) {
            let row = findRow(key);
            if (!row) {
                row = {
                    "Variable": key,
                    "Pre": "Not Available",
                    "Post": postData[key],
